package basic;

public class Evenoddfn {
	void Evenodd(int a) {
		int no=25;
		if(no%2==0) {
			System.out.println(no+ "is even");
		}
		else
			System.out.println(no+ "is odd");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Evenoddfn e=new Evenoddfn();
		e.Evenodd(49);

	}

}
