package corejava;

public class Datatypes {
	static int a ;
	static byte b;
	static float c;
	static short d;
	static double e;
	static long f;
	static char g;
	static boolean h;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int y=259;
		int z []= {1,2,6};
		String name[]= {"sha", "apple", "ant"};
		System.out.println("Array:+a[0]");
		System.out.println("size of string array:"+name.length);
		byte b=127;
		System.out.println(b);
        char a='a';
		System.out.println(a);
		int c=24;
		System.out.println(c);
		float d=1.34f;
		System.out.println(d);
		boolean g=true;
		System.out.println(g);
		double h=27764;
		System.out.println(h);
		short i=12;
		System.out.println(i);
		long j=27864;
		System.out.println(j);
		String k="Edubridge";
		System.out.println(k);
		String s="";
		System.out.println(s);
		System.out.println(a);
		System.out.println(" the default value is:"+a);
		System.out.println(" the default value of byte is:"+b);
		System.out.println(" the default value of float is:"+c);
		System.out.println(" the default value of short is:"+d);
		System.out.println(" the default value of double is:"+e);
		System.out.println(" the default value of long is:"+f);
		System.out.println(" the default value of char is:"+g);
		System.out.println(" the default value of boolean is:"+h);
	}	

}
